CREATE TABLE movie(`movieid` INT(11) NOT NULL auto_increment, `name` VARCHAR(45) NOT NULL, `actors`  VARCHAR(45) DEFAULT NULL, `rating` INT(11) DEFAULT NULL, PRIMARY KEY (`movieid`)) engine=innodb auto_increment=36 DEFAULT charset=utf8;  
CREATE TABLE comment(`commentId` int(11) NOT NULL AUTO_INCREMENT, `comment` varchar(45) DEFAULT NULL, `movie_movieId` int(11) NOT NULL, PRIMARY KEY (`commentId`), KEY `fk_comment_movie_idx` (`movie_movieId`), CONSTRAINT `fk_comment_movie` FOREIGN KEY (`movie_movieId`) REFERENCES `movie` (`movieId`) ON DELETE NO ACTION ON UPDATE NO ACTION) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8;